#ifndef WMASKICONS_H
#define WMASKICONS_H

#include <QIcon>
#include <QPixmap>
#include <QByteArray>

inline QIcon base64ToQIcon(std::string _base64) {
    QPixmap qpixmap;
    qpixmap.loadFromData(QByteArray::fromBase64(_base64.c_str()));
    return QIcon(qpixmap);
}

const auto ICON_FAVICON = []() { return base64ToQIcon(""); };
const auto ICON_MEDIA_PLAYBACK_START = []() { return base64ToQIcon(""); };
const auto ICON_MEDIA_PLAYBACK_PAUSE = []() { return base64ToQIcon(""); };
const auto ICON_SYSTEM_RUN = []() { return base64ToQIcon(""); };
const auto ICON_SYSTEM_SHUTDOWN = []() { return base64ToQIcon(""); };
const auto ICON_WINDOW_CLOSE = []() { return base64ToQIcon(""); };

#endif // WMASKICONS_H
